import * as React from "react";
import styled from "styled-components/native";
import { TextInput, Button, Title } from "react-native-paper";

export const Layout = ({ state, handleChange, handlePressSubmit, navigation }) => (
    <Container>
        {/* <Logo source={logo} /> */}
        <Title>Welcome to our platform, please login!</Title>
        <FormContainer>
            <TextInput
                mode="flat"
                // id="email"
                label="Email"
                value={state.email}
                onChangeText={(text) => handleChange("email")(text)}
            />
            <TextInput
                mode="flat"
                // id="password"
                label="Password"
                // type="password"
                value={state.password}
                onChangeText={(text) => handleChange("password")(text)}
            />
            <Button mode="contained" onPress={() => handlePressSubmit()}>
                LOGIN
            </Button>
            <Button onPress={() => navigation.push("Register")}>
                Go To register
            </Button>
        </FormContainer>
    </Container>
)

const FormContainer = styled.View`
  display: flex;
  flex-direction: column;
`;

const Container = styled.View`
    flex: 1;
    justify-content: center;
    padding: 0px 20px;
`;

const Logo = styled.Image`
  width: 100px;
  height: 100px;
  object-fit: contain;
`;
