import React from "react";
import { View, FlatList } from "react-native";
import { Paragraph, Title, Card, Avatar} from "react-native-paper";
import { Rating } from "react-native-ratings";

const LeftContent = props => <Avatar.Icon {...props} icon="bike" />

const CardSubtitle = props => (<Rating 
    type='custom' 
    readonly 
    startingValue={props.rating} 
    imageSize={20} 
    ratingColor='#3498db' 
/>)

export const Layout = ({ reviews }) => (
    <View>
        <Title> Reviews </Title>
        { reviews.length === 0 ? 
            <Paragraph> There are no comments for this city, send us your review! </Paragraph>
            :
            <FlatList
                data={reviews}
                renderItem={ ({ item }) =>
                <View>
                    <Card style={{marginVertical: 10}}>
                        <Card.Title title={item.user.fullname} left={LeftContent} subtitle={CardSubtitle(item.rating)} />
                        <Card.Content>
                            <Paragraph> {item.description} </Paragraph>
                        </Card.Content>
                    </Card>
                </View>
                }
                keyExtractor={item => item.id}
            />
        }
    </View>
)
