import * as React from 'react';
import { TextInput, Button } from 'react-native-paper';
import { ActivityIndicator, View } from 'react-native';
import styled from "styled-components/native";

export const Layout = ({ state, handleChange, handleAdd, loading }) => (
    <View>
        <FormContainer>
            <TextInput
                editable={!loading}
                mode="flat"
                label="Description"
                value={state.description}
                onChangeText={handleChange("description")}
            />
            <Button mode="contained" style={{backgroundColor: "rgb(25, 118, 210)"}} onPress={handleAdd} disabled={loading}>
                LOGIN
            </Button>
            <ActivityIndicator size="large" color="rgb(25, 118, 210)" animating={loading} />
        </FormContainer>
    </View>
)

const FormContainer = styled.View`
    flex: 1;
`;
