import * as React from "react";
import { Layout } from './layout';
import { useRoute } from "@react-navigation/native";
import { useSetNavigationOptions } from "./hooks/useSetNavigationOptions";
import { Image } from "react-native";
import { Pagination } from "react-native-snap-carousel";

const data = (
    {
        "id": "5edd5a61129de4226497f69e",
        "name": "Lugar calido",
        "description": "Hace calor",
        "continentId": "5ecedbe183230732301c6ead",
        "regionId": "5ecedf85b7d28c3ef0e24baa",
        "images": [
            {
            "id": "5eceedf90049254974ba7207",
            "url": "https://photos.mandarinoriental.com/is/image/MandarinOriental/paris-2017-home?$MO_masthead-property-mobile"
            },
            {
            "id": "5ecef0d6b7e5f214441b8e68",
            "url": "https://lp-cms-production.imgix.net/2019-06/55425108.jpg?fit=crop&q=40&sharp=10&vib=20&auto=format&ixlib=react-8.6.4"
            }
        ],
        "activities": [
            {
            "id": "5ecee9fbe709a340b4b53c32",
            "price": 15,
            "name": "Prueba"
            }
        ],
        "reviews": [
            {
            "id": "5ecef12db7e5f214441b8e69",
            "description": "Algo",
            "rating": 3,
            "user": {
                "id": "5ece9750250d442bf8c9ba09",
                "fullname": "Diego"
            }
            }
        ]
    }
)

export const PlaceDetail = () => {
    const route:any = useRoute();
    const placeId = route.params.id;
    const title = route.params.name;
    const [activeIndex, setActiveIndex] = React.useState(0);
    useSetNavigationOptions(title);

    const renderItem = ({item, index}) => {
        return (
            <Image source={{uri: item.url}} style={{height: 200, width: 300}} />
        );
    }

    const pagination = () => {
        return (
            <Pagination
              dotsLength={data.images.length}
              activeDotIndex={activeIndex}
              containerStyle={{ backgroundColor: 'rgba(0, 0, 0, 0.75)' }}
              dotStyle={{
                  width: 10,
                  height: 10,
                  borderRadius: 5,
                  marginHorizontal: 8,
                  backgroundColor: 'rgba(255, 255, 255, 0.92)'
              }}
              inactiveDotStyle={{
                  // Define styles for inactive dots here
              }}
              inactiveDotOpacity={0.4}
              inactiveDotScale={0.6}
            />
        );
    }

    return <Layout placeId={placeId} renderItem={renderItem} setActiveIndex={setActiveIndex} pagination={pagination} data={data}/>;
}
