import React from "react";
import { View, Text, SafeAreaView, FlatList } from "react-native";
import styled from "styled-components/native";
import { Title, Paragraph, List, Divider } from 'react-native-paper';
import { PlaceImages } from "./PlaceImages";

export const Layout = ({ place }) => (
    <Container>
        <View>
            <PlaceImages images={place.images} />
        </View>
        <View style={{marginVertical: 10}}>
            <Title> Description </Title>
            <Paragraph style={{marginLeft: 20}}> {place.description} </Paragraph>
        </View>
        <Divider />
        <View>
        <SafeAreaView style={styles.container}>
            <FlatList
                data={place.activities}
                renderItem={({ item }) => <Item title={item.title} />}
                keyExtractor={item => item.id}
            />
        </SafeAreaView>
            <List.Accordion
                title="Activities"
                left={props => <List.Icon {...props} icon="folder" />}
            >
                {place.activities.map( activity => {
                    console.log(activity);
                    return <List.Item  title={activity.name + ", Price:" + activity.price + " U$S"} />
                })}
            </List.Accordion>
        </View>
    </Container>
);

const Container = styled.View`
  flex: 1;
  padding: 0px 20px;
`;

const Item = styled.Text`
    background-color: rgb(25, 118, 210);
    padding: 10;
    color: white;
`
