import React from "react";
import { Headline, Card, Button } from "react-native-paper";
import styled from "styled-components/native";
import { FlatList, TouchableOpacity } from "react-native-gesture-handler";
import { FontAwesome } from '@expo/vector-icons'; 

const LeftContent = props => <FontAwesome name="paper-plane" size={25} color={"rgb(25, 118, 210)"} {...props} />

export const Layout = ({ navigation, token, data }) => (
    <Container>
        { token ? 
            <View>
                <Button 
                    mode="contained" 
                    style={{backgroundColor: "rgb(25, 118, 210)", marginVertical: 10}} 
                    onPress={() => console.log("Me apretaron") /*navigation.push("AddReview", {placeId:placeId})*/}
                >
                    Add Trip
                </Button>
                <FlatList
                    style={{marginVertical: 10}}
                    data={data.trips}
                    renderItem={ ({ item }) =>
                        <TouchableOpacity onPress={() => navigation.push("TripDetail", {tripId:item.id})} >
                            <Card style={{marginVertical: 10}}>
                                <Card.Title title={item.name} left={LeftContent}/>
                            </Card>
                        </TouchableOpacity>
                    }
                    keyExtractor={item => item.id}
                />
            </View>
            
            :
            <Headline style={{marginVertical: 10}}> You need login to see your trips </Headline>
        }
    </Container>
)

const Container = styled.ScrollView`
  flex: 1;
  padding: 0px 20px;
`;
