import * as React from "react";
import { useNavigation } from "@react-navigation/native";
import { Layout } from './layout';
import { useLogin } from "./hooks/useLogin";
import { useAuth } from "../../hooks/useAuth";
import { useSetNavigationOptions } from "../../hooks/useSetNavigationOptions";

export const Login = () => {
    const navigation = useNavigation();
    const [loading, setLoading] = React.useState(false);
    const [error, setError] = React.useState(false);
    const [errorMessage, setErrorMessage] = React.useState("");
    const { state, handleChange, handleSubmit } = useLogin();
    const { setToken } = useAuth();
    useSetNavigationOptions("Login");

    const handlePressSubmit = async () => {
      try {
          setLoading(true);
          const user = await handleSubmit();
          console.log("Handle Submit", user.login);
          setErrorMessage(user.login.error);
          setError(!user.login.ok);
          //setToken(user?.token);
          // navigation.replace("Home", { user });
          setLoading(false);
      } 
      catch (error) {
          console.log("error", error);
      }
  };

  return <Layout errorMessage={errorMessage} error={error} state={state} handleChange={handleChange} navigation={navigation} handlePressSubmit={handlePressSubmit} />;
}
