import React from "react";
import { View, FlatList, Text } from "react-native";
import { Paragraph, Title, Divider, Subheading } from "react-native-paper";
import { Rating } from "react-native-ratings";

export const Layout = ({ reviews }) => (
    <View>
        <Title> Reviews </Title>
        { reviews.length === 0 ? 
            <Paragraph> There are no comments for this city, send us your review! </Paragraph>
            :
            <FlatList
                data={reviews}
                renderItem={ ({ item }) => 
                    <View style={{marginVertical: 10}}>
                        <Subheading>{item.user.fullname}</Subheading>
                        <Rating type='custom' readonly startingValue={item.rating} imageSize={20} ratingColor='#3498db' style={{ marginLeft: 10, marginRight: 'auto' }}/>
                        <Text>{item.description}</Text>
                        <Divider style={{marginTop: 10}} />
                    </View>
                }
                keyExtractor={item => item.id}
            />
        }
    </View>
)
