import React from "react";
import { View, FlatList, Alert } from "react-native";
import { Paragraph, Title, Card, Avatar, Button} from "react-native-paper";
import { Rating } from "react-native-ratings";

const LeftContent = props => <Avatar.Icon style={{backgroundColor: "rgb(25, 118, 210)"}}  {...props} icon="chat" />

const CardSubtitle = ({rating}) => (
    <Rating 
        type='custom'
        ratingCount={5} 
        readonly 
        startingValue={rating} 
        imageSize={20} 
        ratingColor='#3498db' 
    />
)

export const Layout = ({ reviews, token }) => (
    <View>
        <Title> Reviews </Title>
        { reviews.length === 0 ? 
            <Paragraph> There are no comments for this city, send us your review! </Paragraph>
            :
            <FlatList
                data={reviews}
                renderItem={ ({ item }) =>
                <View>
                    <Card style={{marginVertical: 10}}>
                        <Card.Title title={item.user.fullname} left={LeftContent} subtitle={CardSubtitle({rating: item.rating})} />
                        <Card.Content>
                            <Paragraph> {item.description} </Paragraph>
                        </Card.Content>
                    </Card>
                </View>
                }
                keyExtractor={item => item.id}
            />
        }
        { !token ? 
            <Button mode="contained" style={{backgroundColor: "rgb(25, 118, 210)"}} onPress={() => Alert.alert('Simple Button pressed')} >
                Add Review
            </Button>
            :
            <Paragraph> Sign in to send your review too! </Paragraph>
        }
        
    </View>
)
