import { Alert } from "react-native";

export const useAlert = ({errorMessage}) => {
    console.log("Pase por aca");
    Alert.alert(
      "Oh snap! You got an error!",
      errorMessage,
      [
        { text: "OK"}
      ],
      { cancelable: false }
    );
};
