import * as React from "react";
import { Layout } from './Layout';
import { useRoute } from "@react-navigation/native";
import { useSetNavigationOptions } from "../../hooks/useSetNavigationOptions";
import { Loading } from "../Loading";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";

const data = (
    {
        "id": "5edd5a61129de4226497f69e",
        "name": "Lugar calido",
        "description": "Hace calor",
        "continentId": "5ecedbe183230732301c6ead",
        "regionId": "5ecedf85b7d28c3ef0e24baa",
        "images": [
            {
            "id": "5eceedf90049254974ba7207",
            "url": "https://photos.mandarinoriental.com/is/image/MandarinOriental/paris-2017-home?$MO_masthead-property-mobile"
            },
            {
            "id": "5ecef0d6b7e5f214441b8e68",
            "url": "https://lp-cms-production.imgix.net/2019-06/55425108.jpg?fit=crop&q=40&sharp=10&vib=20&auto=format&ixlib=react-8.6.4"
            }
        ],
        "activities": [
            {
                "id": "5ecee9fbe709a340b4b53c32",
                "price": 15,
                "title": "Prueba"
            },
            {
                "id": "5ecee9fbe709a340b4c32b53",
                "price": 55,
                "title": "Prueba 2"
            }
        ],
        "reviews": [
            {
            "id": "5ecef12db7e5f214441b8e69",
            "description": "Algo",
            "rating": 4,
            "user": {
                "id": "5ece9750250d442bf8c9ba09",
                "fullname": "Diego"
            }
            }
        ]
    }
)

export const PlaceDetail = () => {
    const { loading, error, data } = useQuery(gql`
        {
        regions {
            id,
            name,
            places {
            id,
            name,
            mainImageUrl
            }
        }
        }`
    );


    const route:any = useRoute();
    const placeId = route.params.id;
    const title = route.params.name;
    useSetNavigationOptions(title);

    if( loading ) {
        return <Loading />
    } 
    else {
        return <Layout place={data.place}/>;
    }
}
