import * as React from "react";
import { Layout } from './layout';
import { useRoute } from "@react-navigation/native";
import { useSetNavigationOptions } from "./hooks/useSetNavigationOptions";
import { Image } from "react-native";
import { Pagination } from "react-native-snap-carousel";

export const PlaceDetail = () => {
    const route:any = useRoute();
    const placeId = route.params.id;
    const title = route.params.name;
    const [activeIndex, setActiveIndex] = React.useState(0);
    useSetNavigationOptions(title);

    const renderItem = ({item, index}) => {
        return (
            <Image source={{uri: item.url}} style={{height: 200, width: 300}} />
        );
    }

    const pagination = ({items, activeIndex}) => {
        return (
            <Pagination
              dotsLength={items.length}
              activeDotIndex={activeIndex}
              containerStyle={{ backgroundColor: 'rgba(0, 0, 0, 0.75)' }}
              dotStyle={{
                  width: 10,
                  height: 10,
                  borderRadius: 5,
                  marginHorizontal: 8,
                  backgroundColor: 'rgba(255, 255, 255, 0.92)'
              }}
              inactiveDotStyle={{
                  // Define styles for inactive dots here
              }}
              inactiveDotOpacity={0.4}
              inactiveDotScale={0.6}
            />
        );
    }

    return <Layout placeId={placeId} renderItem={renderItem} setActiveIndex={setActiveIndex} pagination={pagination} />;
}
