import React from "react";
import { StyleSheet, AsyncStorage } from "react-native";
import { Provider as PaperProvider } from "react-native-paper";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { ApolloProvider } from "@apollo/react-hooks";
import { MaterialIcons, Fontisto } from '@expo/vector-icons'; 
import client from "./client";
import { Login } from "./screens/Login";
import { Register } from "./screens/Register";
import { Continent } from "./screens/Continent";
import { PlaceDetail } from "./screens/PlaceDetail";

const RootStack = createStackNavigator();
const Tab = createBottomTabNavigator();

export const AuthContext = React.createContext({
  token: "",
  setToken: (x: string) => {
    console.log("X", x);
  },
});

export default function App() {
  const [isReady, setReady] = React.useState(false);
  const [token, setToken] = React.useState<string>("");

  React.useEffect(() => {
    AsyncStorage.getItem("token")
      .then((token) => {
        if (token) setToken(token);
      })
      .finally(() => setReady(true));
  }, []);

  React.useEffect(() => {
    AsyncStorage.getItem("token")
      .then((storedToken) => {
        if (storedToken !== token) {
          AsyncStorage.setItem("token", token);
        }
      });
  }, [token]);

  if (!isReady) return null;

  //const initialRouteName = token ? "Home" : "Login";
  return (
    <AuthContext.Provider
      value={{
        token,
        setToken: (x) => {
          setToken(x);
        },
      }}
    >
      <ApolloProvider client={client}>
        <PaperProvider>
          <NavigationContainer>
            <Tab.Navigator
                      screenOptions={({ route }) => ({
                        tabBarIcon: ({ focused, color, size }) => {
                          let iconName = '';

                          if (route.name === 'Continent') {
                            iconName = 'world-o';
                          } else if (route.name === 'Region') {
                            iconName = 'place';
                          }

                          // You can return any component that you like here!
                          return <MaterialIcons name={iconName} size={24} color="black" />;
                        },
                      })}
                >
              <Tab.Screen name="Continent" component={Continent} />
              <Tab.Screen name="Region" component={Continent} />
            </Tab.Navigator>
            {/* <RootStack.Navigator initialRouteName={"Home"}>
              {!token ? (
                <>
                  <RootStack.Screen 
                    name="Login" 
                    component={Login} 
                  />
                  <RootStack.Screen
                    name="Register"
                    component={Register}
                  />
                </>
              ) : (
                <>
                  <RootStack.Screen name="Home" component={Home} />
                  <RootStack.Screen name="PlaceDetail" component={PlaceDetail} />
                </>
              )}
            </RootStack.Navigator> */}
          </NavigationContainer>
        </PaperProvider>
      </ApolloProvider>
    </AuthContext.Provider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
