import * as React from "react";
import { useNavigation } from "@react-navigation/native";
import { Layout } from './layout';
import { useLogin } from "./hooks/useLogin";
import { useAuth } from "../../hooks/useAuth";
import { useSetNavigationOptions } from "../../hooks/useSetNavigationOptions";
import { useAlert } from "../../hooks/useAlert";

export const Login = () => {
    const navigation = useNavigation();
    const [loading, setLoading] = React.useState(false);
    const [error, setError] = React.useState(false);
    const [errorMessage, setErrorMessage] = React.useState("");
    const { state, handleChange, handleSubmit } = useLogin();
    const { setToken } = useAuth();
    useSetNavigationOptions("Login");

    const handlePressSubmit = async () => {
      try {
        setLoading(true);

        const data = await handleSubmit();
        if(data.login.ok){
        setToken(data.login.user?.token);
        //navigation.replace("Home", { user });
        }
        else {
            useAlert(data.login.error);
            setErrorMessage(data.login.error);
            setError(!data.login.ok);
        }

        setLoading(false);
      } 
      catch (error) {
        useAlert(error);
      }
  };

  return <Layout loading={loading} errorMessage={errorMessage} error={error} state={state} handleChange={handleChange} navigation={navigation} handlePressSubmit={handlePressSubmit} />;
}
