import React from "react";
import { View, Text, SafeAreaView } from "react-native";
import Carousel from "react-native-snap-carousel";

const data = (
    {
        "id": "5edd5a61129de4226497f69e",
        "name": "Lugar calido",
        "description": "Hace calor",
        "continentId": "5ecedbe183230732301c6ead",
        "regionId": "5ecedf85b7d28c3ef0e24baa",
        "images": [
            {
            "id": "5eceedf90049254974ba7207",
            "url": "Prueba"
            },
            {
            "id": "5ecef0d6b7e5f214441b8e68",
            "url": "Prueba"
            }
        ],
        "activities": [
            {
            "id": "5ecee9fbe709a340b4b53c32",
            "price": 15,
            "name": "Prueba"
            }
        ],
        "reviews": [
            {
            "id": "5ecef12db7e5f214441b8e69",
            "description": "Algo",
            "rating": 3,
            "user": {
                "id": "5ece9750250d442bf8c9ba09",
                "fullname": "Diego"
            }
            }
        ]
    }
)

export const Layout = ({ placeId, renderItem, setActiveIndex }) => (
    <View style={{flex:1, justifyContent: "center", alignItems: "center"}}>
        <Text> {placeId} </Text>
        <SafeAreaView style={{flex: 1, paddingTop: 50 }}>
            <View style={{ flex: 1, flexDirection:'row', justifyContent: 'center', }}>
                <Carousel 
                    layout={"default"}
                    data={data.images}
                    sliderWidth={300}
                    itemWidth={300}
                    renderItem={renderItem}
                    onSnapToItem = { index => setActiveIndex(index) } />
            </View>
        </SafeAreaView>
    </View>
);
