import React from "react";
import { View } from "react-native";
import styled from "styled-components/native";
import { Title, Paragraph, List, Divider } from 'react-native-paper';
import { PlaceImages } from "./PlaceImages";
import { PlaceActivities } from "./PlaceActivities";

export const Layout = ({ place }) => (
    <Container>
        <View>
            <PlaceImages images={place.images} />
        </View>
        <View style={{marginVertical: 10}}>
            <Title> Description </Title>
            <Paragraph style={{marginLeft: 20}}> {place.description} </Paragraph>
        </View>
        <Divider />
        <View>
            <PlaceActivities activities={place.activities} />
        </View>
    </Container>
);

const Container = styled.ScrollView`
  flex: 1;
  padding: 0px 20px;
`;
